import React, { Component } from "react";
import { Container } from "../../components/Grid";
import Wrapper from "../../components/Wrapper";
import "./style.css";
import API from "../../utils/API";
import Saved from "../../components/Saved";
import Navbar from "../../components/Navbar";
import TopButton from "../../components/TopButton";


class SaveLink extends Component {
  state = {
    savedLinks: [],
    inProgressLinks: []
  };

  componentDidMount() {
    API.getLinks()

      .then(res => this.setState({ savedLinks: res.data }))
      .catch(err => console.log(err));
  }

  handleDeleteButton = _id => {
    API.deleteLink(_id)
      .then(res => this.componentDidMount())
      .catch(err => console.log(err));
  };

  

  handleInProgressButton = event => {
    console.log(event);
    event.preventDefault();
    
    let inProgressLinks = {
      inProgressLink: inProgressLink,
      inProgress: true
    };

    inProgressLinks = this.state.links.filter(inProgressLink => inProgressLink.id === event.target.id)
    inProgressLinks = inProgressLinks[0];
    API.saveLink(inProgressLinks)
      .then(this.setState(
        {
          message: alert('Saved to In Progress')
        }))
      .catch(err => console.log(err));
  }


  render() {
    return (
      <Container fluid>
        <Container>
          <Navbar></Navbar>
          <Wrapper>
            <Saved
              savedLinks={this.state.savedLinks}
              handleDeleteButton={this.handleDeleteButton}
              links={this.state.links}
              handleinProgressButton={this.handleInProgressButton}
            />
            <TopButton></TopButton>
          </Wrapper>
        </Container>
      </Container>
    );
  }
}

export default SaveLink;
